function HiModel(){this.getAjaxData=function(method,dataSend,thenRun){BX.ajax.runComponentAction("hipot:ajax",method,{mode:"ajax",data:dataSend,method:"POST"}).then(function(response){let responseData={};if(response.status==="success"){responseData=JSON.parse(response.data)}thenRun(responseData,response)},function(response){thenRun({},response)})};this.getModelStat=function(entityName,entityOrder,filter,initRun){this.getAjaxData("getEntityStat",{entityType:entityName,entityOrder:entityOrder,filter:filter},initRun)};this.getModel=function(entityName,id,initRun){this.getAjaxData("getEntity",{entityType:entityName,entityId:id},initRun)}}const HiModelLib=new HiModel;